// DOM selector Methods
// getElementsByTagName()

const collection = document.getElementsByTagName("p");
console.log(collection);

//Query Selectors
//querySelector(css selector): selects only 1 element

document.querySelector("#sample").textContent = "Almabetter";
document.querySelector("#sample").innerHTML = "<h2>Almabetter</h2>";
document.querySelector(".item").textContent = "Hello";

//querySelectorAll(css selector): it will all the elements
console.log(document.querySelectorAll(".item")); //collection

//creating & deleting & updating elements in HTML

//create div tag using js
const element = document.createElement("div"); //<div></div>
element.innerHTML = "Creating Div element"; //Adding element content | <div>Creating Div element</div>
console.log(element);

// document.body.appendChild(element);//always add element in the end
document.body.prepend(element); //always add element in the beginning

// add a list
const list = document.createElement("ul"); //<ul></ul>

for (let i = 0; i < 3; i++) {
  const li = document.createElement("li");
  console.log(li); //<li></li>

  li.innerHTML = "list item " + i;
  list.appendChild(li);
}

console.log(list);

document.querySelector("#add").after(list);

//updating an element
document.querySelector(".item").innerHTML = "World";

//delete
//delete last li of ul
document.querySelector("li:last-child").remove();

//add image
const img = document.createElement("img");
img.setAttribute(
  "src",
  "data:image/png;base64,"
);
img.setAttribute("alt", "facebook"); //<img />

document.querySelector("#sample").after(img);

console.log(document.querySelector("#add").getAttribute("title"));

// create input
const input = document.createElement("input");
input.setAttribute("placeholder", "Please enter name");
input.setAttribute("type", "text");
// input.name="pwd"

console.log(input);

document.querySelector(".test").removeAttribute("title");

